package com.controlstructure;

public class ForLoop2table {

	public void display2table()
	{	
		for(int i=1; i<=10; i++)
		{
			System.out.println(2*i);
			
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ForLoop2table  obj=new ForLoop2table();
		obj.display2table();
	}

}
